sys_script_fix.script:
gs.info("Starting Day Card Fix Script...");
try {
	var current = new GlideRecord('x_dehp_time_track_time_card_day');
	current.query();
	var output = "Fix Script Results:";
	while(current.next()) {
		var dayCard = new DayCard(current);
		var rateCard = dayCard.findRateCard();
		if (current.rate_card != rateCard)
			output += "\nFix Rate Card: " + current.getDisplayValue('rate_card') + "\tNew Rate Card: " + rateCard;
			current.rate_card = rateCard;
			current.update();
	}
	gs.info(output);
}
catch (e) {
	gs.error('Fix script failed:\n' + e);
}
