sys_script_fix.script:
var timesheet;
var timeSheetIDs = [];
var dayCards = new GlideRecord('x_dehp_time_track_time_card_day');
dayCards.addEncodedQuery('time_card.category.codeNOT INCapital,Expense');
dayCards.query();

while(dayCards.next())
	timeSheetIDs.push(dayCards.time_card.time_sheet + '');

var arrayUtil = new global.ArrayUtil();
var uniqueID = new global.ArrayUtil().unique(timeSheetIDs);
gs.info("Fix script 'Recalculate Undetermined Time Sheets' recalculating the following " + uniqueID.length + " Time Sheet values: \n\n" + timeSheetIDs.join(' \n'));

uniqueID.forEach(function(timeSheetID) {
	timesheet = new TimeSheet(timeSheetID);
	timesheet.updateTime();
});
