sys_script_fix.script:
var audits;
var timeSheets = new GlideRecord('x_dehp_time_track_time_sheet');
timeSheets.addEncodedQuery('state!=Pending');
timeSheets.query();
if(timeSheets.next()) {
	audits = new GlideRecord('sys_audit');
	audits.addQuery('tablename', 'x_dehp_time_track_time_sheet');
	audits.addQuery('fieldname', 'state');
	audits.addQuery('documentkey', timeSheets.sys_id);
	audits.addQuery('oldvalue', 'Pending');
	audits.orderBy('record_checkpoint');
	audits.query();
	if(audits.next()) {
		timeSheets.submitted_on_time = timeSheets.submission_due > audits.sys_created_on;
	}
}
