sys_script_include.script:
var Tests = Class.create();
Tests.AccountTest = function() {
	var user = "d3846b9e6f170100b85fcb512e3ee43f";
	var arrayUtil = new global.ArrayUtil();
	var account;
	var categories = [];
	var parentAccounts = new GlideRecord('x_dehp_time_track_accounts');
	parentAccounts.addQuery('active=true^parent=NULL');
	parentAccounts.query();
	while (parentAccounts.next()) {
		account = new Account(parentAccounts);
		categories = arrayUtil.union(categories, account.getCategories(user, true));
	}
	categories.sort();
	return categories;

};
